INSERT INTO `dynasty` VALUES (1, '三皇时代', NULL, NULL, '无固定都城', '华夏上古传说时期，三皇（燧人、伏羲、神农等）教民钻木取火、耕种畜牧、创造八卦，奠定华夏文明基础', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (2, '五帝时代', NULL, NULL, '涿鹿、平阳等', '部落联盟鼎盛时期，黄帝、颛顼、帝喾、尧、舜相继执政，统一华夏部落，推行禅让制，大禹治水成功', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (3, '夏朝', -2070, -1600, '阳城、斟鄩', '中国史载第一个世袭制王朝，大禹建夏，启继位开创家天下，桀亡国，二里头遗址为重要文化遗存', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (4, '商朝', -1600, -1046, '亳、殷', '又称殷商，盘庚迁殷后国力兴盛，甲骨文成熟，青铜文明发达，商纣王荒淫亡国', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (5, '西周', -1046, -771, '镐京', '武王伐纣建立，周公制礼作乐，推行分封制、宗法制，厉王时期国人暴动，幽王烽火戏诸侯致亡国', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (6, '东周', -770, -256, '洛邑', '平王东迁后建立，分为春秋、战国两段，王室衰微，诸侯争霸，百家争鸣，最终秦灭东周', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (7, '秦朝', -221, -207, '咸阳', '秦始皇统一六国，建立中国第一个中央集权封建王朝，推行郡县制、统一文字货币度量衡，修筑长城，二世而亡', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (8, '西汉', -202, 8, '长安', '刘邦建立，史称西汉，文景之治休养生息，汉武帝时期独尊儒术、开拓西域，王莽篡汉终结', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (9, '新朝', 8, 23, '长安', '王莽篡汉建立，推行托古改制，因政策失当引发绿林、赤眉起义，最终被推翻', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (10, '东汉', 25, 220, '洛阳', '刘秀建立，史称东汉，光武中兴恢复国力，外戚与宦官交替专权，黄巾起义后走向分裂', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (11, '曹魏', 220, 265, '洛阳', '曹丕篡汉建立，占据北方，实行九品中正制，司马懿家族逐渐掌权，最终被西晋取代', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (12, '蜀汉', 221, 263, '成都', '刘备建立，占据西南，诸葛亮鞠躬尽瘁北伐，后被曹魏所灭', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (13, '东吴', 222, 280, '建业', '孙权建立，占据江南，航海业发达，最终被西晋统一', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (14, '西晋', 265, 317, '洛阳', '司马炎建立，统一三国，八王之乱后国力衰退，永嘉之乱后南渡', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (15, '东晋', 317, 420, '建康', '司马睿在江南建立，门阀政治盛行，淝水之战击败前秦，后被刘裕取代', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (16, '南北朝', 420, 589, '建康、平城、洛阳、长安等', '南朝（宋、齐、梁、陈）与北朝（北魏、东魏、西魏、北齐、北周）对峙，孝文帝改革促进民族融合，隋最终统一', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (17, '隋朝', 581, 618, '长安、洛阳', '杨坚建立，统一南北，开创三省六部制、科举制，开凿大运河，二世而亡', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (18, '唐朝', 618, 907, '长安、洛阳', '李渊建立，中国封建王朝鼎盛时期，贞观之治、开元盛世国力强盛，安史之乱后由盛转衰，藩镇割据终结', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (19, '五代十国', 907, 960, '开封、洛阳、金陵等', '五代（梁、唐、晋、汉、周）更迭于北方，十国割据于南方，战乱频繁，后周世宗改革为统一奠定基础', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (20, '北宋', 960, 1127, '开封', '赵匡胤陈桥兵变建立，推行重文轻武，经济文化发达，澶渊之盟与辽对峙，靖康之耻被金灭亡', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (21, '辽朝', 916, 1125, '上京临潢府', '契丹族建立，占据北方草原与燕云十六州，推行南北面官制，与宋、夏并立，后被金所灭', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (22, '西夏', 1038, 1227, '兴庆府', '党项族建立，占据西北，与宋、辽、金并立，创造西夏文，后被蒙古所灭', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (23, '南宋', 1127, 1279, '临安', '赵构南渡建立，偏安江南，岳飞抗金，经济重心完全南移，崖山海战被元灭亡', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (24, '金朝', 1115, 1234, '上京会宁府、中都', '女真族建立，灭辽与北宋，占据北方，后被蒙古与南宋联合灭亡', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (25, '元朝', 1271, 1368, '大都', '忽必烈建立，蒙古族统一中国，疆域空前辽阔，推行行省制，民族融合加强，元末农民起义推翻', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (26, '明朝', 1368, 1644, '南京、北京', '朱元璋建立，推翻元朝，洪武之治、永乐盛世，郑和下西洋，后期闭关锁国，崇祯帝自缢亡国', '2025-12-15 17:56:07');
INSERT INTO `dynasty` VALUES (27, '清朝', 1644, 1912, '北京', '满族建立，清军入关统一全国，康乾盛世疆域稳定，后期鸦片战争后国力衰退，辛亥革命后宣统帝退位，封建王朝终结', '2025-12-15 17:56:07');
INSERT INTO `event` VALUES (1, '燧人取火', NULL, NULL, 1, '科技', '燧人氏发明钻木取火，人类进入熟食时代', '远古时期，人类茹毛饮血。燧人氏观察鸟啄燧木而生火，领悟钻木取火之法。教民熟食，结束了人类茹毛饮血的历史，标志着人类文明的一大进步。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (2, '伏羲画卦', NULL, NULL, 1, '文化', '伏羲氏观察天地万物，创立八卦', '伏羲氏仰观天文，俯察地理，远取诸物，近取诸身，始作八卦。用八种符号代表天、地、雷、风、水、火、山、泽，以通神明之德，以类万物之情。八卦成为中华文化的源头。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (3, '女娲补天', NULL, NULL, 1, '神话', '女娲炼五色石补天，抟土造人', '传说共工与祝融战，共工怒触不周山，天柱折，地维绝，天倾西北，地陷东南。女娲炼五色石补天，断鳌足以立四极，杀黑龙以济冀州，积芦灰以止淫水。又抟黄土造人，创造人类。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (4, '神农尝百草', NULL, NULL, 1, '科技', '神农氏尝百草，发明医药', '神农氏为解除民疾，亲尝百草，辨别药性。一日遇七十毒，终因尝断肠草而卒。其功绩在于辨别五谷、草木、虫鱼、禽兽之性，教民种植，发明医药。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (5, '伏羲制嫁娶', NULL, NULL, 1, '制度', '伏羲氏制定婚姻制度，结束群婚', '伏羲氏之前，民知其母不知其父。伏羲制定嫁娶制度，男聘女嫁，以俪皮为礼，使人类由群婚时代进入对偶婚时代，这是人类社会制度的一大进步。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (6, '神农作耒耜', NULL, NULL, 1, '科技', '神农氏发明农具，教民耕作', '神农氏观察鸟兽耕作之法，斫木为耜，揉木为耒，教民使用农具耕种。又尝百草，辨别五谷，教民种植，开创中华农业文明。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (7, '阪泉之战', NULL, NULL, 2, '军事', '黄帝与炎帝在阪泉大战，黄帝获胜', '黄帝与炎帝两大部落争夺中原霸权，战于阪泉之野。经过三次大战，黄帝击败炎帝。战后两部落结盟，形成华夏族主体，\"炎黄子孙\"称谓由此而来。', 2, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (8, '涿鹿之战', NULL, NULL, 2, '军事', '黄帝与蚩尤在涿鹿大战，黄帝擒杀蚩尤', '九黎部落首领蚩尤作乱，黄帝联合炎帝，与蚩尤战于涿鹿之野。黄帝用指南车指引方向，最终擒杀蚩尤，统一中原各部落，奠定中华文明基础。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (9, '嫘祖养蚕', NULL, NULL, 2, '科技', '黄帝之妻嫘祖发明养蚕缫丝', '黄帝正妃嫘祖，西陵氏之女，发明养蚕缫丝之术，教民纺织，使人类结束衣皮苇的时代，进入穿衣文明时代，被尊为先蚕。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (10, '仓颉造字', NULL, NULL, 2, '文化', '黄帝史官仓颉创造文字', '黄帝时期，史官仓颉观察鸟兽足迹，创制文字，使人类进入文明时代。传说仓颉造字成功，天雨粟，鬼夜哭，象征着人类文明的重大突破。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (11, '颛顼绝地天通', NULL, NULL, 2, '宗教', '颛顼改革宗教，绝地天通', '五帝时代之前，家家有巫史，人人可通神。颛顼命南正重司天，火正黎司地，将人神分离，统一宗教祭祀权，加强中央权力，史称\"绝地天通\"。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (12, '尧禅让于舜', NULL, NULL, 2, '政治', '尧帝年老，将帝位禅让给舜，开创禅让制', '尧帝在位七十年，年老体衰，征询四岳，寻找接班人。舜以孝道闻名，经过三年考核，尧将帝位禅让给舜。这是中国古代政治制度的理想典范。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (13, '舜耕历山', NULL, NULL, 2, '文化', '舜在历山耕作，以孝道闻名', '舜年轻时在历山耕作，其孝道感动天地。象为之耕，鸟为之耘，尧帝听说其贤德，召见并禅让帝位。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (14, '舜南巡狩', -2208, -2208, 2, '政治', '舜帝南巡视察，死于苍梧', '舜帝为视察南方，南巡狩，崩于苍梧。葬于江南九嶷，是为零陵。其弟象被封于有庳。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (15, '鲧治洪水', -2287, -2277, 2, '灾难', '鲧用堵塞方法治水，九年不成', '洪水泛滥，尧命鲧治水。鲧采用堵塞方法，筑堤防洪，九年不成，最终被舜处死。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (16, '大禹治水', -2277, -2267, 2, '科技', '大禹采用疏导方法治水，十三年成功', '舜命禹继承父业治水。禹采用疏导方法，开山凿石，疏导九河，历时十三年，三过家门而不入，终获成功。禹因此功被舜禅让帝位。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (17, '舜任命百官', NULL, NULL, 2, '政治', '舜帝设立百官，完善国家制度', '舜摄政期间，设立大禹为司空、后稷为农官、契为司徒、皋陶为法官、倕为共工、益为虞官、伯夷为秩宗、夔为乐官、龙为纳言，完善国家行政制度。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (18, '象封有庳', NULL, NULL, 2, '政治', '舜帝封其弟象于有庳', '舜帝不顾群臣反对，封其顽劣之弟象于有庳。象在有庳改恶从善，成为贤君，体现舜的孝悌之道。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (19, '大禹治水', -2277, -2178, 3, '文化', '大禹治理洪水，划分九州', '大禹吸取父亲鲧治水失败的教训，采用\"疏而不堵\"的方法，开凿龙门、疏导九河，最终将洪水引入大海。治水成功后，舜禅让帝位于禹，禹成为中国历史上第一个世袭制王朝的开创者。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (20, '启继位', -2070, -2070, 3, '政治', '启继承大禹之位，开创\"家天下\"', '大禹死后，本应由伯益继位，但大禹之子启夺取了政权，建立夏朝，开创了父死子继的世袭制度，中国进入\"家天下\"时代。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (21, '少康中兴', NULL, NULL, 3, '政治', '少康复国，夏朝中兴', '后羿、寒浞篡夏，夏朝中断。大禹后裔少康长大后在有虞氏帮助下复国，恢复夏朝统治，史称\"少康中兴\"。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (22, '桀纣之暴', -1728, -1675, 3, '政治', '夏桀暴虐无道，商汤灭夏', '夏桀荒淫无道，宠妹喜，造酒池肉林。商汤见夏桀暴虐，决心推翻夏朝，最终灭夏建商。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (23, '商汤灭夏', -1646, -1600, 4, '军事', '商汤推翻夏桀暴政，建立商朝', '商汤见夏桀暴虐无道，决心推翻夏朝。他重用伊尹为相，经过十一次征战，最终在鸣条之战中大败夏桀，建立商朝。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (24, '盘庚迁殷', -1300, -1250, 4, '政治', '盘庚将都城从奄（今山东曲阜）迁至殷（今河南安阳）', '盘庚为摆脱贵族势力控制和避免水患，决定将都城迁至殷。这次迁都使商朝摆脱了混乱局面，政治稳定，经济文化得到发展，商朝从此被称为殷商。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (25, '武丁中兴', -1250, -1200, 4, '政治', '武丁时期商朝达到鼎盛', '武丁在位期间，商朝国力达到鼎盛。他任用傅说为相，妇好为将，对外征伐，扩大疆域，对内发展经济文化，史称\"武丁中兴\"。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (26, '商纣暴政', -1105, -1046, 4, '政治', '商纣王荒淫无道，商朝灭亡', '商纣王宠妲己，设炮烙之刑，杀比干，囚箕子，导致民怨沸腾。周武王伐纣，在牧野之战中击败商军，纣王自焚，商朝灭亡。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (27, '甲骨文成熟', -1200, -1100, 4, '文化', '商代甲骨文成熟，汉字源头', '商代晚期，甲骨文已经非常成熟，记录占卜、祭祀、战争等内容。甲骨文是中国最早的成熟文字系统，为汉字发展奠定基础。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (28, '武王伐纣', -1046, -1046, 5, '军事', '周武王联合诸侯讨伐商纣王，牧野之战灭亡商朝', '周武王在吕尚（姜子牙）辅佐下，率领诸侯联军与商军在牧野决战。商军倒戈，纣王自焚于鹿台，商朝灭亡，西周建立。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (29, '周公制礼作乐', -1042, -1032, 5, '制度', '周公旦制定周礼，建立典章制度', '武王死后，成王年幼，周公摄政。他制礼作乐，建立分封制、宗法制、井田制等典章制度，奠定了周朝八百年的统治基础。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (30, '国人暴动', -842, -841, 5, '政治', '西周厉王时期，国人发动起义驱逐厉王', '周厉王实行专利政策，垄断山林川泽之利，又禁止国人言论自由，导致国人不满。国人发动暴动，攻入王宫，厉王逃亡彘地，由召公、周公共同执政，史称\"共和行政\"。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (31, '烽火戏诸侯', -771, -771, 5, '政治', '周幽王为博褒姒一笑，烽火戏诸侯，导致西周灭亡', '周幽王宠爱褒姒，为博她一笑，多次点燃烽火戏弄诸侯。当犬戎真正入侵时，诸侯不再相信，不来救援，镐京被攻破，幽王被杀，西周灭亡。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (32, '平王东迁', -770, -770, 6, '政治', '周平王在犬戎入侵后迁都洛邑，东周开始', '西周灭亡后，太子宜臼在诸侯护送下东迁洛邑，建立东周。从此周王室衰微，诸侯势力坐大，进入春秋战国时代。', 2, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (33, '春秋争霸', -770, -476, 6, '军事', '齐桓公、晋文公、秦穆公、楚庄王相继称霸', '东周前期，周王室衰微，诸侯争霸。齐桓公在管仲辅佐下首先称霸，提出\"尊王攘夷\"口号。其后晋文公、秦穆公、楚庄王相继称霸，史称春秋五霸。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (34, '管仲改革', -685, -645, 6, '政治', '齐桓公任用管仲改革，富国强兵', '齐桓公任用管仲为相，推行\"相地而衰征\"、军政合一、士农工商分居等改革，使齐国富强，成为春秋首霸。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (35, '城濮之战', -632, -632, 6, '军事', '晋文公在城濮大败楚军', '晋文公流亡十九年回国即位后，整顿内政，发展经济。在城濮之战中采用\"退避三舍\"之计，大败楚军，确立霸主地位。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (36, '商鞅变法', -356, -338, 6, '政治', '秦孝公任用商鞅进行变法，奠定秦国强大基础', '商鞅在秦国推行两次变法，内容包括废井田、开阡陌，奖励耕战，实行郡县制，统一度量衡等。变法使秦国国力大增，为日后统一六国奠定基础。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (37, '长平之战', -260, -260, 6, '军事', '秦赵长平之战，白起坑杀赵国降卒四十万', '秦赵两国为争夺上党郡爆发大战。秦将白起采用诱敌深入战术，大败赵军，坑杀赵国降卒四十万。此战使赵国元气大伤，秦国统一六国已成定局。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (38, '百家争鸣', -600, -221, 6, '文化', '春秋战国时期各种思想学说涌现', '春秋战国时期，社会剧变，各种思想学说涌现，形成儒、道、墨、法、名、阴阳、纵横、杂、农、小说等\"诸子百家\"，互相争鸣，是中国思想史上最辉煌的时期。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (39, '孔子周游列国', -497, -484, 6, '文化', '孔子为推行仁政周游列国', '孔子为推行其\"仁政\"思想，带领弟子周游卫、宋、陈、蔡、楚等国，历经十四年，虽不被重用，但传播了儒家思想。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (40, '老子出关', -450, -450, 6, '文化', '老子西出函谷关，留下《道德经》', '老子见周室衰微，西出函谷关。关令尹喜强留其著书，老子遂著《道德经》五千言，阐述道家思想。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (41, '孙子献书', -512, -512, 6, '军事', '孙武向吴王阖闾献《孙子兵法》', '孙武以《兵法》十三篇见吴王阖闾，吴王令其操练宫女，孙武斩吴王二宠姬，军纪严明，被任为将军。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (42, '秦灭六国', -230, -221, 7, '军事', '秦始皇十年间相继灭掉六国，统一中国', '从公元前230年灭韩开始，秦始皇采取远交近攻策略，先后灭韩、赵、魏、楚、燕、齐六国，于公元前221年完成统一，建立秦朝。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (43, '统一度量衡', -221, -221, 7, '政治', '秦始皇统一文字、货币、度量衡', '秦始皇下令统一全国文字（小篆）、货币（秦半两）、度量衡标准，车同轨，书同文，对中国历史产生深远影响。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (44, '焚书坑儒', -213, -212, 7, '文化', '秦始皇焚毁诗书，坑杀儒生', '秦始皇采纳李斯建议，下令焚烧除秦国史书、医药、卜筮、种树书以外的所有诗书，坑杀议论朝政的儒生四百六十余人，以加强思想控制。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (45, '陈胜吴广起义', -209, -208, 7, '军事', '中国历史上第一次大规模农民起义', '秦二世元年，陈胜、吴广在大泽乡因雨误期，为求生而发动起义，提出\"王侯将相宁有种乎\"口号，建立张楚政权，揭开秦末农民战争序幕。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (46, '巨鹿之战', -207, -207, 7, '军事', '项羽破釜沉舟，在巨鹿大败秦军', '项羽率楚军与秦军在巨鹿决战。项羽下令破釜沉舟，激励士兵奋勇作战，大败秦军主力章邯部，奠定灭秦基础。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (47, '赵高指鹿为马', -207, -207, 7, '政治', '赵高专权，指鹿为马，秦朝灭亡', '秦二世时，赵高专权，为试群臣，在朝堂上献鹿而称马。群臣畏惧赵高，多附和说是马。此事说明赵高专权已极，秦朝即将灭亡。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (48, '子婴投降', -206, -206, 7, '军事', '秦王子婴向刘邦投降，秦朝灭亡', '赵高杀秦二世后，立子婴为秦王。子婴诛杀赵高，但刘邦已率兵至灞上。子婴素车白马，系颈以组，奉皇帝玺符，降轵道旁，秦朝正式灭亡。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (49, '楚汉相争', -206, -202, 8, '军事', '刘邦与项羽争夺天下，最终刘邦建立汉朝', '秦朝灭亡后，项羽分封十八路诸侯，自立为西楚霸王，封刘邦为汉王。刘邦与项羽展开四年争夺，最终刘邦在垓下之战击败项羽，建立汉朝。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (50, '白登之围', -200, -200, 8, '军事', '汉高祖刘邦被匈奴围困于白登山', '汉高祖刘邦亲率大军北击匈奴，被匈奴冒顿单于围困于白登山七天七夜，后采用陈平计策才得以脱险。此后汉朝对匈奴采取和亲政策。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (51, '文景之治', -179, -141, 8, '政治', '汉文帝、汉景帝时期，轻徭薄赋，休养生息', '汉文帝、汉景帝父子在位期间，推行黄老无为而治，轻徭薄赋，与民休息，废除肉刑，社会经济得到恢复和发展，为汉武帝时期国力强盛奠定基础。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (52, '七国之乱', -154, -154, 8, '军事', '汉景帝时期，七个刘姓诸侯王叛乱', '汉景帝采纳晁错建议削藩，引发吴王刘濞等七个诸侯王叛乱。汉景帝派周亚夫率军平叛，三个月内平定叛乱，中央集权得到加强。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (53, '独尊儒术', -134, -134, 8, '文化', '汉武帝采纳董仲舒建议，罢黜百家独尊儒术', '汉武帝采纳儒生董仲舒建议，\"罢黜百家，独尊儒术\"，设立太学，以儒家思想为官方正统思想，对中国后世文化产生深远影响。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (54, '张骞通西域', -138, -115, 8, '外交', '张骞两次出使西域，开辟丝绸之路', '汉武帝派张骞出使西域，联络大月氏共同抗击匈奴。张骞两次出使西域，虽未达到军事目的，但开辟了丝绸之路，促进了中西经济文化交流。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (55, '卫青霍去病北伐', -133, -119, 8, '军事', '卫青、霍去病多次出击匈奴，收复河西走廊', '汉武帝派卫青、霍去病多次率大军北伐匈奴。卫青收复河套地区，霍去病六击匈奴，打通河西走廊，设置酒泉、武威、张掖、敦煌四郡。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (56, '盐铁之议', -81, -81, 8, '经济', '西汉政府召开盐铁会议，讨论盐铁官营政策', '汉昭帝时期，朝廷召开盐铁会议，讨论盐铁官营等政策利弊。贤良文学主张罢盐铁、酒榷、均输，而御史大夫桑弘羊主张继续官营。此次会议反映了朝廷与民间的经济思想冲突。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (57, '王莽改制', 8, 23, 9, '政治', '王莽篡汉建立新朝，推行托古改制', '王莽篡夺西汉政权，建立新朝，推行一系列改革，包括井田制、五均六筦、币制改革等。因改革脱离实际，引发社会动荡，最终导致绿林、赤眉起义。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (58, '绿林赤眉起义', 17, 27, 9, '军事', '新朝末年农民起义，推翻王莽政权', '新朝末年，湖北绿林军和山东赤眉军相继起义，反对王莽暴政。绿林军拥立刘玄为更始帝，赤眉军拥立刘盆子为帝，最终联合推翻新莽。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (59, '光武中兴', 25, 57, 10, '政治', '汉光武帝刘秀建立东汉，实现中兴', '刘秀在绿林赤眉起义中崛起，于公元25年在河北称帝，建立东汉。他统一全国，减轻赋税，与民休息，史称\"光武中兴\"。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (60, '班超经营西域', 73, 102, 10, '军事', '班超投笔从戎，经营西域三十一年', '班超奉命出使西域，以\"不入虎穴焉得虎子\"的勇气，联合西域诸国抵御匈奴，恢复东汉对西域的统治，被封为定远侯。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (61, '党锢之祸', 166, 184, 10, '政治', '东汉末年，宦官专权，两次打击士大夫集团', '东汉桓帝、灵帝时期，宦官专权，与士大夫集团矛盾激化。宦官两次制造\"党锢\"，逮捕、流放、禁锢\"党人\"，导致统治集团分裂，加速东汉灭亡。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (62, '黄巾起义', 184, 205, 10, '军事', '张角领导的农民起义，标志东汉名存实亡', '张角创立太平道，以\"苍天已死，黄天当立\"为口号，发动农民起义。起义军头裹黄巾，故称黄巾军。起义虽被镇压，但东汉政权名存实亡，进入军阀割据。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (63, '董卓之乱', 189, 192, 10, '军事', '董卓率兵入京，专权暴政，引发诸侯讨伐', '大将军何进召董卓入京除宦官，董卓趁机控制朝政，废少帝立献帝，专权暴政。袁绍、曹操等诸侯组成联军讨伐，董卓被迫迁都长安，后被吕布所杀。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (64, '王允连环计', 192, 192, 10, '政治', '王允用貂蝉设连环计，离间董卓吕布', '司徒王允为除董卓，收歌姬貂蝉为义女，先许吕布，后献董卓，离间二人关系。最终吕布杀董卓，但王允也被董卓部将所杀。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (65, '官渡之战', 200, 200, 11, '军事', '曹操在官渡大败袁绍，奠定统一北方基础', '曹操与袁绍在官渡决战。曹操以少胜多，大败袁绍十万大军，袁绍从此一蹶不振。此战奠定曹操统一北方的基础。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (66, '赤壁之战', 208, 208, 11, '军事', '孙权、刘备联军在赤壁大败曹操', '曹操率大军南征，欲统一全国。孙权、刘备联军在周瑜、诸葛亮策划下，采用火攻，在赤壁大败曹军。此战奠定三国鼎立格局。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (67, '三国鼎立', 220, 280, 11, '政治', '魏、蜀、吴三国鼎立，相互制衡', '曹丕代汉建魏，刘备在成都称帝建蜀，孙权在建业称王建吴，形成三国鼎立局面。三国之间相互制衡，时而联合，时而战争。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (68, '诸葛亮北伐', 228, 234, 12, '军事', '诸葛亮六出祁山，北伐曹魏', '诸葛亮为完成刘备遗志，六出祁山北伐曹魏。虽取得一些胜利，但因国力悬殊，最终未能成功，病逝于五丈原。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (69, '六出祁山', 228, 234, 12, '军事', '诸葛亮六次北伐曹魏的军事行动', '诸葛亮为完成刘备遗志，先后六次率军北伐曹魏，采取兵出祁山的战略。虽取得一些局部胜利，但因国力悬殊和后方补给困难，最终未能成功。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (70, '夷陵之战', 222, 222, 13, '军事', '陆逊在夷陵击败刘备，巩固东吴基业', '刘备为夺回荆州、为关羽报仇，亲率大军伐吴。吴将陆逊采用火攻，在夷陵大败蜀军，刘备退守白帝城，不久病逝。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (71, '司马氏专权', 249, 265, 11, '政治', '司马懿及其子司马师、司马昭专权曹魏', '司马懿在高平陵之变中诛杀曹爽，开始专权曹魏。其子司马师、司马昭相继掌权，废立皇帝，为司马炎代魏建晋奠定基础。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (72, '高平陵之变', 249, 249, 11, '政治', '司马懿发动政变，诛杀曹爽', '司马懿趁曹爽陪同皇帝祭扫高平陵之机，发动政变，控制洛阳，迫使曹爽投降并将其诛杀，从此司马氏专权曹魏。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (73, '西晋统一', 265, 280, 14, '军事', '司马炎代魏建晋，统一三国', '司马炎代魏称帝，建立西晋。公元280年，晋军六路伐吴，灭亡东吴，结束三国鼎立，统一中国。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (74, '八王之乱', 291, 306, 14, '军事', '西晋宗室八王为争夺皇位而内战', '晋武帝司马炎死后，惠帝司马衷即位。八个司马氏宗室王为争夺皇位，相互攻杀，导致西晋国力大衰，五胡趁机入侵。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (75, '永嘉之乱', 311, 316, 14, '军事', '匈奴刘渊、刘聪攻破洛阳、长安，西晋灭亡', '八王之乱后，西晋国力衰弱。匈奴刘渊建立汉赵，其子刘聪先后攻破洛阳、长安，俘虏晋怀帝、晋愍帝，西晋灭亡。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (76, '衣冠南渡', 317, 317, 15, '政治', '西晋灭亡，司马睿南渡建康，建立东晋', '西晋灭亡后，琅邪王司马睿在南北士族支持下，南渡建康称帝，建立东晋。大批北方士族和民众南迁，史称\"衣冠南渡\"。', 2, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (77, '淝水之战', 383, 383, 15, '军事', '东晋在淝水大败前秦，保卫江南', '前秦苻坚率百万大军南征，欲灭东晋。东晋谢安、谢玄以八万北府兵在淝水大败前秦军，保卫了江南，使南北对峙局面延续。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (78, '王敦之乱', 322, 324, 15, '军事', '王敦起兵叛乱', '东晋初年，王敦以诛刘隗为名两次起兵叛乱，攻入建康，威胁朝廷。后王敦病死，叛乱平定。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (79, '苏峻之乱', 327, 329, 15, '军事', '苏峻、祖约叛乱', '历阳太守苏峻与祖约不满朝廷，起兵叛乱，攻入建康，后苏峻战死，叛乱被平定。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (80, '孝文帝改革', 471, 499, 16, '政治', '北魏孝文帝推行汉化改革，迁都洛阳，促进民族融合', '北魏孝文帝迁都洛阳，推行汉化改革：改汉姓、说汉话、穿汉服、与汉族通婚、采用汉族官制等。改革促进了北方民族融合，为隋唐统一奠定基础。', 2, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (81, '隋统一南北', 581, 589, 17, '政治', '隋文帝杨坚统一南北，结束近三百年分裂', '北周外戚杨坚夺取政权，建立隋朝，是为隋文帝。他派杨广、杨素率军灭陈，统一南北，结束自西晋灭亡以来近三百年的分裂局面。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (82, '开凿大运河', 605, 610, 17, '工程', '隋炀帝征发民夫开凿京杭大运河', '隋炀帝为沟通南北交通，征发数百万民夫开凿大运河，以洛阳为中心，北达涿郡，南至余杭。大运河促进了南北经济文化交流，但劳民伤财，引发民怨。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (83, '三征高句丽', 612, 614, 17, '军事', '隋炀帝三次征讨高句丽，均以失败告终', '隋炀帝为恢复汉时疆域，三次征讨高句丽。因劳师远征、后勤不继、指挥失当，三次均失败，损失惨重，引发大规模农民起义。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (84, '瓦岗军起义', 611, 618, 17, '军事', '翟让、李密领导瓦岗军起义', '隋末农民起义中，翟让、李密领导的瓦岗军是主要力量之一。瓦岗军攻占洛口仓，开仓济民，队伍发展到数十万人，最终失败。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (85, '玄武门之变', 626, 626, 18, '政治', '李世民发动政变，杀死兄弟，逼父退位', '秦王李世民与太子李建成、齐王李元吉矛盾激化。李世民在玄武门设伏，杀死建成、元吉，逼迫唐高祖李渊退位，自立为帝，是为唐太宗。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (86, '贞观之治', 627, 649, 18, '政治', '唐太宗时期，政治清明，经济繁荣，文化昌盛', '唐太宗吸取隋亡教训，任用贤臣，虚心纳谏，轻徭薄赋，完善科举制度。贞观年间，政治清明，社会安定，经济繁荣，文化昌盛，史称\"贞观之治\"。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (87, '文成公主入藏', 641, 641, 18, '外交', '唐太宗将文成公主嫁给松赞干布', '唐太宗将宗室女文成公主嫁给吐蕃赞普松赞干布。文成公主带去大量书籍、工匠、种子等，促进了汉藏经济文化交流。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (88, '玄奘西行', 629, 645, 18, '文化', '玄奘西行取经，历时十七年', '唐朝高僧玄奘为求取佛经真义，从长安出发，历尽艰辛，西行印度取经。历时十七年，带回大量佛经，著有《大唐西域记》，为中国佛教发展作出巨大贡献。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (89, '武则天称帝', 690, 705, 18, '政治', '武则天废唐建周，成为中国历史上唯一的女皇帝', '武则天原为唐太宗才人，后成为唐高宗皇后。高宗死后，她临朝称制，于公元690年废唐建周，自立为帝，成为中国历史上唯一的女皇帝。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (90, '开元盛世', 713, 741, 18, '政治', '唐玄宗前期，政治清明，经济繁荣，文化辉煌', '唐玄宗前期任用姚崇、宋璟等贤相，整顿吏治，发展经济，文化繁荣。开元年间，唐朝国力强盛，疆域辽阔，经济繁荣，文化辉煌，史称\"开元盛世\"。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (91, '安史之乱', 755, 763, 18, '军事', '安禄山、史思明发动叛乱，唐朝由盛转衰', '唐玄宗宠信安禄山，使其兼任三镇节度使。安禄山于公元755年在范阳起兵反叛，攻占洛阳、长安。叛军虽被平定，但唐朝元气大伤，由盛转衰。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (92, '马嵬驿兵变', 756, 756, 18, '军事', '安史之乱中，禁军哗变，杨贵妃被赐死', '安史之乱中，唐玄宗逃往蜀地，途经马嵬驿。禁军哗变，杀死杨国忠，逼迫唐玄宗赐死杨贵妃。玄宗无奈，命高力士缢死杨贵妃。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (93, '黄巢起义', 875, 884, 18, '军事', '黄巢领导的农民起义，攻入长安', '黄巢响应王仙芝起义，后成为领袖。他率领义军转战南北，于公元880年攻入长安，建立大齐政权。后起义失败，黄巢自杀。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (94, '陈桥兵变', 960, 960, 20, '军事', '赵匡胤发动兵变，黄袍加身，建立宋朝', '后周殿前都点检赵匡胤奉命北伐，行至陈桥驿，部下将士将黄袍披在他身上，拥立为帝。赵匡胤回师开封，代周建宋。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (95, '杯酒释兵权', 961, 961, 20, '政治', '宋太祖通过宴会解除将领兵权', '宋太祖赵匡胤为避免武将专权威胁皇权，设宴请石守信等禁军将领饮酒，劝说他们交出兵权，给予优厚待遇。将领们纷纷称病辞职，交出兵权。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (96, '澶渊之盟', 1005, 1005, 20, '外交', '宋辽签订澶渊之盟，实现百年和平', '宋真宗在寇准力主下亲征，在澶州击败辽军。双方签订澶渊之盟：宋每年给辽岁币银十万两、绢二十万匹，双方约为兄弟之国，此后百余年和平相处。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (97, '庆历新政', 1043, 1045, 20, '政治', '范仲淹推行庆历新政，试图改革弊政', '范仲淹针对宋朝积贫积弱局面，提出明黜陟、抑侥幸、精贡举、择官长、均公田、厚农桑、修武备、减徭役、覃恩信、重命令等十项改革措施，但因保守派反对而失败。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (98, '王安石变法', 1069, 1085, 20, '政治', '王安石推行新法，试图富国强兵', '宋神宗任用王安石推行变法，包括青苗法、募役法、市易法、方田均税法等。变法虽增加国家收入，但因执行不当遭保守派反对，最终失败。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (99, '靖康之变', 1127, 1127, 20, '军事', '金兵攻破汴京，北宋灭亡', '金兵南下攻破汴京，俘虏宋徽宗、宋钦宗及宗室、大臣三千余人，北宋灭亡。康王赵构南渡，建立南宋。', 2, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (100, '岳飞抗金', 1134, 1142, 23, '军事', '岳飞率领岳家军抗击金兵，收复失地', '岳飞组建岳家军，纪律严明，英勇善战，多次北伐，收复襄阳六郡和郑州、洛阳等地。但宋高宗和秦桧主和，以\"莫须有\"罪名杀害岳飞。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (101, '绍兴和议', 1141, 1141, 23, '外交', '宋金签订绍兴和议，南宋称臣纳贡', '宋金签订绍兴和议：宋向金称臣，每年纳贡银二十五万两、绢二十五万匹；双方以淮河至大散关为界。此议使南宋得以偏安江南。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (102, '采石之战', 1161, 1161, 23, '军事', '虞允文在采石矶大败金军', '金主完颜亮率大军南侵，企图灭亡南宋。南宋文臣虞允文临危受命，在采石矶指挥宋军大败金军，保全了南宋政权。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (103, '崖山海战', 1279, 1279, 23, '军事', '南宋与元朝的最后一次决战，南宋灭亡', '元军追击南宋残余势力至崖山。宋军战败，陆秀夫背负幼帝赵昺投海殉国，南宋灭亡。此次海战标志着元朝统一中国。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (104, '蒙古西征', 1219, 1260, 25, '军事', '成吉思汗及其子孙三次西征，建立蒙古帝国', '成吉思汗及其子孙先后发动三次大规模西征，征服中亚、西亚、东欧广大地区，建立横跨欧亚的蒙古帝国，对世界历史产生重大影响。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (105, '忽必烈建元', 1271, 1271, 25, '政治', '忽必烈改国号为元，定都大都', '忽必烈在1260年继承蒙古汗位，1271年改国号为元，定都大都（今北京），建立元朝。1279年灭南宋，统一中国。', 2, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (106, '行省制度', 1271, 1368, 25, '政治', '元朝推行行省制，影响后世行政区划', '元朝在地方推行行省制度，全国设中书省及十个行中书省。行省制度加强了中央集权，对后世行政区划产生深远影响，沿用至今。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (107, '红巾军起义', 1351, 1368, 25, '军事', '韩山童、刘福通领导的红巾军起义', '元末政治腐败，灾害频繁。韩山童、刘福通以白莲教组织起义，起义军头裹红巾，故称红巾军。红巾军起义最终推翻元朝统治。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (108, '朱元璋建明', 1368, 1368, 26, '政治', '朱元璋推翻元朝，建立明朝', '朱元璋参加红巾军起义，逐渐壮大势力，消灭陈友谅、张士诚等割据势力。1368年在南京称帝，建立明朝，派徐达北伐攻占大都，元朝灭亡。', 2, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (109, '洪武之治', 1368, 1398, 26, '政治', '明太祖朱元璋统治时期，政治严酷，经济恢复', '明太祖朱元璋推翻元朝后，采取一系列措施恢复经济：奖励垦荒，移民屯田，兴修水利，减免赋役。同时加强皇权，废丞相，设锦衣卫，实行严刑峻法。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (110, '靖难之役', 1399, 1402, 26, '军事', '燕王朱棣起兵反对建文帝，夺取皇位', '明太祖死后，皇太孙朱允炆即位，是为建文帝。建文帝削藩，燕王朱棣以\"清君侧\"为名起兵，经过四年战争，攻占南京，建文帝失踪，朱棣即位，是为明成祖。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (111, '郑和下西洋', 1405, 1433, 26, '外交', '郑和七次下西洋，远航至非洲东海岸', '明成祖派郑和率庞大船队七次下西洋，访问亚非三十多个国家和地区，最远到达非洲东海岸和红海沿岸。郑和下西洋比欧洲大航海早半个多世纪，显示了明朝国力强盛。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (112, '土木堡之变', 1449, 1449, 26, '军事', '明英宗在土木堡被瓦剌俘虏', '明英宗在宦官王振鼓动下，亲征瓦剌。明军在土木堡被瓦剌包围，全军覆没，英宗被俘，史称\"土木堡之变\"。此战后明朝由盛转衰。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (113, '北京保卫战', 1449, 1449, 26, '军事', '于谦领导北京保卫战，击退瓦剌', '土木堡之变后，瓦剌挟持英宗进攻北京。兵部尚书于谦力主抗战，拥立景帝，领导北京军民击退瓦剌，保卫了明朝。', 2, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (114, '夺门之变', 1457, 1457, 26, '政治', '明英宗复辟，重新登基', '明代宗病重，太上皇英宗在石亨、徐有贞、曹吉祥等人策划下，乘景帝病危发动政变，夺东华门入宫，重新登基，废景帝为郕王。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (115, '戚继光抗倭', 1555, 1566, 26, '军事', '戚继光在东南沿海抗击倭寇', '明朝嘉靖年间，倭寇频繁侵扰东南沿海。戚继光组建戚家军，发明鸳鸯阵，在台州、福建、广东等地多次大败倭寇，基本平定倭患。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (116, '张居正改革', 1573, 1582, 26, '政治', '张居正推行一条鞭法，整顿吏治', '明朝万历初年，首辅张居正推行改革：整顿吏治，实行考成法；推行一条鞭法，简化赋役；整饬边防，任用戚继光等名将。改革使明朝国力有所恢复。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (117, '萨尔浒之战', 1619, 1619, 26, '军事', '后金在萨尔浒大败明军', '明朝派杨镐率四路大军讨伐后金努尔哈赤。努尔哈赤采取\"凭你几路来，我只一路去\"的战术，在萨尔浒集中兵力，各个击破，大败明军。此战后明朝在辽东转入守势。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (118, '李自成进京', 1644, 1644, 26, '军事', '李自成攻入北京，崇祯帝自缢', '李自成率大顺军攻破北京，崇祯帝在煤山自缢，明朝灭亡。李自成在北京称帝，但很快在山海关被吴三桂与清军击败，退出北京。', 2, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (119, '清军入关', 1644, 1644, 27, '军事', '清军入关，取代明朝统治', '李自成攻占北京，崇祯帝自缢，明朝灭亡。山海关总兵吴三桂降清，引清军入关。清军击败李自成，占领北京，顺治帝定都北京，开始清朝对全国的统治。', 2, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (120, '平定三藩', 1673, 1681, 27, '军事', '康熙帝平定三藩之乱', '康熙帝决定撤除三藩（吴三桂、尚可喜、耿精忠），引发三藩叛乱。康熙帝派兵讨伐，历时八年，最终平定叛乱，巩固了清朝统治。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (121, '统一台湾', 1683, 1683, 27, '军事', '康熙帝统一台湾，设台湾府', '康熙帝派施琅率军攻台，灭郑氏政权，统一台湾。次年设台湾府，隶属福建省。台湾正式纳入清朝版图。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (122, '雅克萨之战', 1685, 1689, 27, '军事', '清军击败沙俄侵略军，签订《尼布楚条约》', '康熙帝派军两次在雅克萨击败沙俄侵略军，迫使沙俄谈判。1689年签订《尼布楚条约》，划定中俄东段边界，是中国与外国签订的第一个平等条约。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (123, '三征噶尔丹', 1690, 1697, 27, '军事', '康熙帝三次亲征噶尔丹', '康熙帝三次亲征，最终平定准噶尔部噶尔丹叛乱，统一漠北蒙古，加强了对蒙古地区的统治。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (124, '康乾盛世', 1681, 1796, 27, '政治', '康熙、雍正、乾隆三朝盛世，清朝国力鼎盛', '康熙、雍正、乾隆三朝，清朝政治稳定，经济繁荣，疆域辽阔，文化昌盛，史称\"康乾盛世\"。但盛极而衰，乾隆后期已现衰败迹象。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (125, '九子夺嫡', 1708, 1722, 27, '政治', '康熙晚年诸子争夺太子之位', '康熙帝两度废太子允礽，导致诸皇子争夺储位。最终四子胤禛胜出，继承皇位，是为雍正帝。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (126, '文字狱', 1711, 1781, 27, '文化', '清朝大兴文字狱，加强思想控制', '清朝为压制汉族知识分子反清思想，大兴文字狱。凡被认为有反清、讥讽朝廷内容的文字，作者即被治罪，株连甚广。文字狱严重禁锢了思想文化的发展。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (127, '四库全书', 1773, 1782, 27, '文化', '乾隆帝下令编纂《四库全书》', '乾隆帝下令编纂《四库全书》，收书3500余种，79000余卷，分经、史、子、集四部。这是中国古代最大的一部丛书，但编纂过程中也销毁了大量被认为有违碍的书籍。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (128, '鸦片战争', 1840, 1842, 27, '外交', '英国发动鸦片战争，中国战败，签订《南京条约》', '英国为打开中国市场，借口虎门销烟发动侵华战争。清朝战败，被迫签订《南京条约》，割让香港，开放五口通商，赔款2100万银元，中国开始沦为半殖民地半封建社会。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (129, '太平天国运动', 1851, 1864, 27, '军事', '洪秀全领导的农民起义，建立太平天国', '洪秀全创立拜上帝会，在广西金田起义，建立太平天国，攻占南京，改称天京。太平天国运动历时十四年，席卷大半个中国，最终被清政府联合外国势力镇压。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (130, '洋务运动', 1861, 1895, 27, '经济', '清朝自强运动，引进西方技术', '清朝在曾国藩、李鸿章、左宗棠、张之洞等推动下，创办近代军事工业和民用工业，建立新式海军，派遣留学生，企图\"师夷长技以自强\"。但因制度腐败，最终失败。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (131, '甲午战争', 1894, 1895, 27, '军事', '中日甲午战争，中国战败，北洋海军全军覆没', '日本为争夺朝鲜和中国，发动甲午战争。清朝战败，签订《马关条约》，割让台湾、澎湖，赔款二亿两白银。这场战争标志着洋务运动的失败。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (132, '戊戌变法', 1898, 1898, 27, '政治', '光绪帝颁布诏令，推行百日维新', '光绪帝在康有为、梁启超、谭嗣同等维新派推动下，颁布一系列变法诏令，涉及政治、经济、军事、文化等方面，试图挽救清朝危亡。但慈禧太后发动政变，变法失败。', 2, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (133, '义和团运动', 1899, 1901, 27, '军事', '义和团运动，反帝爱国斗争', '义和团运动兴起于山东、直隶，以\"扶清灭洋\"为口号，反对外国侵略。八国联军侵华，义和团奋起抵抗，但最终在中外势力联合镇压下失败。', 1, '2025-12-16 13:19:43');
INSERT INTO `event` VALUES (134, '辛亥革命', 1911, 1912, 27, '政治', '辛亥革命推翻清朝，结束中国两千多年封建帝制', '孙中山领导的革命党人在武昌起义成功，各省纷纷响应。1912年，清帝溥仪退位，清朝灭亡，中国两千多年的封建帝制终结，中华民国成立。', 1, '2025-12-16 13:19:43');
