INSERT INTO `geo_region` VALUES (9, '南北朝对峙疆域（公元450年）', 16, 420, 589, '{ \"type\": \"FeatureCollection\", \"features\": [ { \"type\": \"Feature\", \"properties\": { \"name\": \"南朝（宋齐梁陈）\" }, \"geometry\": { \"type\": \"Polygon\", \"coordinates\": [[[110,25],[125,25],[125,35],[110,35],[110,25]]] } }, { \"type\": \"Feature\", \"properties\": { \"name\": \"北朝（北魏）\" }, \"geometry\": { \"type\": \"Polygon\", \"coordinates\": [[[95,35],[110,35],[110,45],[95,45],[95,35]]] } } ] }', '南北朝时期南北对峙，南朝宋、齐、梁、陈相继，北朝北魏统一北方', '2025-12-16 13:19:43');
