INSERT INTO `geo_region` VALUES (13, '辽朝疆域（公元1100年）', 21, 916, 1125, '{ \"type\": \"Feature\", \"properties\": { \"dynasty\": \"辽朝\" }, \"geometry\": { \"type\": \"Polygon\", \"coordinates\": [ [ [ 110, 40 ], [ 130, 40 ], [ 130, 55 ], [ 110, 55 ], [ 110, 40 ] ] ] } }', '辽朝疆域，包括东北、蒙古高原和燕云十六州', '2025-12-16 13:19:43');
INSERT INTO `geo_region` VALUES (14, '西夏疆域（公元1100年）', 22, 1038, 1227, '{ \"type\": \"Feature\", \"properties\": { \"dynasty\": \"西夏\" }, \"geometry\": { \"type\": \"Polygon\", \"coordinates\": [ [ [ 100, 35 ], [ 110, 35 ], [ 110, 42 ], [ 100, 42 ], [ 100, 35 ] ] ] } }', '西夏疆域，包括宁夏、甘肃、陕西北部', '2025-12-16 13:19:43');
