INSERT INTO `geo_region` VALUES (16, '金朝疆域（公元1200年）', 24, 1115, 1234, '{ \"type\": \"Feature\", \"properties\": { \"dynasty\": \"金朝\" }, \"geometry\": { \"type\": \"Polygon\", \"coordinates\": [ [ [ 110, 35 ], [ 125, 35 ], [ 125, 50 ], [ 110, 50 ], [ 110, 35 ] ] ] } }', '金朝疆域，包括东北、华北，灭北宋后占据中原', '2025-12-16 13:19:43');
INSERT INTO `geo_region` VALUES (17, '元朝疆域（公元1300年）', 25, 1279, 1350, '{ \"type\": \"Feature\", \"properties\": { \"dynasty\": \"元朝\" }, \"geometry\": { \"type\": \"Polygon\", \"coordinates\": [ [ [ 75, 20 ], [ 145, 20 ], [ 145, 60 ], [ 120, 60 ], [ 100, 50 ], [ 75, 40 ], [ 75, 20 ] ] ] } }', '元朝统一后的疆域，是中国历史上疆域最辽阔的王朝', '2025-12-16 13:19:43');
INSERT INTO `geo_region` VALUES (18, '明朝疆域（公元1420年）', 26, 1420, 1600, '{ \"type\": \"Feature\", \"properties\": { \"dynasty\": \"明朝\" }, \"geometry\": { \"type\": \"Polygon\", \"coordinates\": [ [ [ 90, 25 ], [ 130, 25 ], [ 130, 50 ], [ 120, 50 ], [ 110, 45 ], [ 90, 40 ], [ 90, 25 ] ] ] } }', '明成祖时期的疆域，东至海，西至哈密，南至南海，北至长城', '2025-12-16 13:19:43');
INSERT INTO `geo_region` VALUES (19, '清朝疆域（公元1760年）', 27, 1760, 1840, '{ \"type\": \"Feature\", \"properties\": { \"dynasty\": \"清朝\" }, \"geometry\": { \"type\": \"Polygon\", \"coordinates\": [ [ [ 75, 18 ], [ 135, 18 ], [ 135, 55 ], [ 120, 55 ], [ 100, 50 ], [ 90, 45 ], [ 75, 35 ], [ 75, 18 ] ] ] } }', '乾隆时期疆域，奠定现代中国版图基础', '2025-12-16 13:19:43');
INSERT INTO `geo_region` VALUES (20, '清朝晚期疆域（公元1900年）', 27, 1840, 1912, '{ \"type\": \"Feature\", \"properties\": { \"dynasty\": \"清朝\" }, \"geometry\": { \"type\": \"Polygon\", \"coordinates\": [ [ [ 75, 18 ], [ 130, 18 ], [ 130, 53 ], [ 115, 53 ], [ 100, 48 ], [ 90, 43 ], [ 75, 35 ], [ 75, 18 ] ] ] } }', '鸦片战争后，清朝疆域萎缩，失去大片领土', '2025-12-16 13:19:43');
INSERT INTO `person` VALUES (1, '燧人氏', NULL, NULL, NULL, NULL, NULL, NULL, 1, NULL, NULL, '华夏上古传说中的三皇之一，发明钻木取火，使人类进入熟食时代，被尊为火祖', '发明钻木取火,教民熟食,结束茹毛饮血时代', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (2, '伏羲', '风', NULL, NULL, NULL, NULL, '太昊', 1, NULL, NULL, '华夏民族人文始祖，三皇之一，创造八卦，教民渔猎畜牧，制定婚姻制度', '创造八卦,制定婚姻制度,发明渔网,创造文字雏形', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (3, '女娲', '风', NULL, NULL, NULL, NULL, NULL, 1, NULL, NULL, '华夏上古神话中的创世女神，传说抟土造人、炼石补天，被尊为人类始祖', '抟土造人,炼石补天,创造人类,制定婚姻', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (4, '神农氏', '姜', NULL, NULL, NULL, NULL, '炎帝', 1, NULL, NULL, '华夏上古传说中的三皇之一，尝百草发明医药，教民耕种，被尊为农业和医药之祖', '尝百草发明医药,发明耒耜,教民耕种,开创农业文明', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (5, '黄帝', '姬', '轩辕', NULL, NULL, NULL, NULL, 2, NULL, NULL, '华夏民族人文始祖，五帝之首，统一华夏部落，被尊为中华民族的共同祖先', '统一华夏部落,涿鹿之战败蚩尤,发明指南车,创造文字历法', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (6, '颛顼', '姬', '乾荒', NULL, NULL, '高阳氏', NULL, 2, NULL, NULL, '五帝之一，黄帝之孙，改革宗教制度，实行绝地天通，加强中央权力', '绝地天通,改革宗教,统一历法,治理洪水', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (7, '帝喾', '姬', '俊', NULL, NULL, '高辛氏', NULL, 2, NULL, NULL, '五帝之一，颛顼之侄，在位期间政治清明，四方归服', '政治清明,发展农业,制定历法,四方归服', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (8, '尧', '伊祁', '放勋', NULL, NULL, NULL, NULL, 2, NULL, NULL, '五帝之一，以仁德治天下，开创禅让制，将帝位禅让给舜', '开创禅让制,任用贤能,治理洪水,制定历法', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (9, '舜', '姚', '重华', NULL, NULL, NULL, NULL, 2, NULL, NULL, '五帝之一，以孝道闻名，继承尧位，任用大禹治水，后禅让给禹', '以孝治天下,任用百官,禅让帝位,巡狩四方', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (10, '大禹', '姒', '文命', NULL, NULL, NULL, NULL, 2, NULL, NULL, '治水英雄，夏朝开国君主，采用疏导方法治理洪水，三过家门而不入', '治理洪水,划分九州,建立夏朝,铸造九鼎', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (11, '蚩尤', NULL, NULL, NULL, NULL, NULL, NULL, 2, NULL, NULL, '九黎部落首领，与黄帝争夺中原，涿鹿之战战败被杀，被尊为战神', '发明兵器,善于作战,九黎部落首领', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (12, '启', '姒', '启', NULL, NULL, NULL, NULL, 3, NULL, NULL, '夏朝第二位君主，大禹之子，开创家天下的世袭制度，结束禅让制', '开创世袭制,建立家天下,甘之战败有扈氏', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (13, '少康', '姒', '少康', NULL, NULL, NULL, NULL, 3, NULL, NULL, '夏朝中兴之主，在有虞氏帮助下复国，恢复夏朝统治，史称少康中兴', '少康中兴,复兴夏朝,消灭寒浞', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (14, '夏桀', '姒', '履癸', NULL, NULL, NULL, NULL, 3, NULL, NULL, '夏朝末代君主，荒淫暴虐，宠妹喜，被商汤所灭', '暴虐无道,亡国之君', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (15, '商汤', '子', '天乙', NULL, NULL, '成汤', '太祖', 4, NULL, NULL, '商朝开国君主，推翻夏桀暴政，建立商朝，任用伊尹为相', '灭夏建商,任用伊尹,网开三面', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (16, '伊尹', '伊', '挚', NULL, NULL, NULL, NULL, 4, NULL, NULL, '商朝开国功臣，辅佐商汤灭夏，历事五代商王，被尊为元圣', '辅佐商汤灭夏,历事五代商王,制定典章制度', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (17, '盘庚', '子', '旬', NULL, NULL, NULL, NULL, 4, NULL, NULL, '商朝中兴之主，迁都于殷，使商朝政治稳定，经济文化发展', '迁都于殷,稳定政局,发展经济', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (18, '武丁', '子', '昭', NULL, NULL, NULL, '高宗', 4, NULL, NULL, '商朝中兴之主，任用傅说为相，妇好为将，商朝国力达到鼎盛', '武丁中兴,任用傅说,开疆拓土', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (19, '妇好', NULL, NULL, NULL, NULL, NULL, NULL, 4, NULL, NULL, '商王武丁王后，中国历史上有据可查的第一位女性军事统帅', '女性军事统帅,征伐方国,主持祭祀', NULL, '2025-12-16 15:25:05');
INSERT INTO `person` VALUES (20, '商纣王', '子', '受', NULL, NULL, '帝辛', NULL, 4, NULL, -1046, '商朝末代君主，聪明勇武但荒淫暴虐，宠妲己，被周武王所灭', '聪明勇武,荒淫暴虐,亡国之君', NULL, '2025-12-16 15:25:05');
